import { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';

const url = 'http://localhost:3001'

function App() {

  const [task, setTask] = useState('')
  const [tasks, setTasks] = useState([])

  useEffect(() => {
    axios.get(url)
      .then(response => {
        setTask(response.data)
      }).catch(error => {
        alert(error.response.data ? error.response.data.error : error)
      })
  }, [])

  const addTask = () => {
    axios.post(url + '/create', {
      description: task
    })
    .then(response => {
      setTask([...tasks,{id: response.data.id,description: task}])
      setTask('')
    }).catch(error => {
      alert(error.response.data.error ? error.response.data.error : error)
    })
  }

  const deleteTask = (id) => {
    axios.delete(url + '/delete/'+ id)
    .then(response => {
      const withoutRemoved = tasks.filter((item) => item !== deleted)
      setTasks(withoutRemoved)
    }).catch(error => {
      alert(error.response.data.error ? error.response.data.error : error)
    })
  }

  return (
    <div id='container'>
      <h3>Todos</h3>
      <form>
        <input 
          placeholder='Add new task' 
          value={task}
          onChange={e => setTask(e.target.value)}
          onKeyDown={e => {
            if (e.key === 'Enter') {
              e.preventDefault()
              addTask()
            }
          }}
        />
      </form>
      <ul>
          {
            tasks.map(item => (
              <li key={item.id}>{item.description} 
                <button className='delete-button' onClick={() => deleteTask(item)}>Delete</button>
              </li>
            ))
          }
      </ul>
    </div>
  );
}

export default App;
